cy.get("body").then($body => {
  if ($body.find(".product-card").length === 0) {
    cy.log("No products available for combined search test");
    return;
  }
  // Lấy sản phẩm đầu tiên và category của nó
  cy.get(".product-card").first().then($card => {
    const categoryName = $card.find(".card-category").text().trim();
    const productName = $card.find(".card-title").text().trim();
    const searchTerm = productName.substring(0, 3);
    // Chọn filter theo category của sản phẩm này
    cy.get(".filter-pill").contains(categoryName).click();
    cy.wait(500);
    // Verify filter active
    productPage.shouldHaveActiveFilter(categoryName);
    // Sau đó search
    productPage.typeSearch(searchTerm);
    cy.wait(500);
    // Verify cả filter và search đều hoạt động
    cy.get(".search-input").should("have.value", searchTerm);
    // Nếu có kết quả, verify đúng category và match search
    cy.get("body").then($body => {
      if ($body.find(".product-card").length > 0) {
        cy.get(".product-card").each($card => {
          cy.wrap($card).find(".card-category").should("contain", categoryName);
          cy.wrap($card).find(".card-title").invoke("text").should("match", new RegExp(searchTerm, "i"));
        });
      } else {
        // Không có kết quả là OK (search quá strict)
        cy.log("No results with combined filter and search");
      }
    });
  });
});

